export default function DataTerkini() {
  return (
    /* ðŸ”´ BACKGROUND FULL */
    <section className="w-full bg-gray-50">
      <div className="max-w-[1300px] mx-auto px-6 py-16">
        {/* Header */}
        <h2
          className="text-gray-900 mb-8"
          style={{
            fontFamily: "Exo, sans-serif",
            fontWeight: 500,
            fontSize: "32px",
            lineHeight: "140%",
            letterSpacing: "0.01em",
          }}
        >
          Data Terkini Terorisme di Indonesia
        </h2>

        {/* GRID TIDAK DIUBAH */}
        <div className="grid grid-cols-6 grid-rows-6 gap-4 h-[400px]">
          <div className="row-span-2">
            <Card value="40" label="Produk Unggulan" iconSize="w-12 h-12" />
          </div>

          <div className="row-span-3">
            <Card value="64" label="Total Ahli" iconSize="w-14 h-14" />
          </div>

          <div className="row-span-4 col-start-1 row-start-3">
            <Card
              value="80"
              label="Total Buku"
              variant="soft"
              iconSize="w-24 h-24"
            />
          </div>

          <div className="row-span-3 col-start-2 row-start-4">
            <Card value="133" label="Total Kebijakan" iconSize="w-14 h-14" />
          </div>

          <div className="col-span-2 row-span-3 col-start-3 row-start-1">
            <Card value="4" label="Total Infografis" iconSize="w-20 h-20" />
          </div>

          <div className="row-span-3 col-start-3 row-start-4">
            <Card value="48" label="Total Inisiatif" iconSize="w-14 h-14" />
          </div>

          <div className="row-span-3 col-start-4 row-start-4">
            <Card value="55" label="Total Modul" iconSize="w-14 h-14" />
          </div>

          <div className="row-span-2 col-start-5 row-start-5">
            <Card value="2" label="Jumlah Video" iconSize="w-12 h-12" />
          </div>

          <div className="row-span-4 col-start-5 row-start-1">
            <Card value="17" label="Total Gambar" iconSize="w-24 h-24" />
          </div>

          <div className="row-span-6 col-start-6 row-start-1">
            <Card value="37" label="Total Buku" iconSize="w-28 h-28" />
          </div>
        </div>

        {/* BUTTON */}
        <div className="flex justify-center mt-8">
          <button className="bg-[#D70000] hover:bg-red-700 text-white font-medium px-8 py-3 rounded-md flex items-center gap-2">
            Lihat Lebih Banyak â†’
          </button>
        </div>
      </div>
    </section>
  );
}

function Card({ value, label, variant = "default", iconSize }) {
  const isSoft = variant === "soft";

  return (
    <div
      className={`
        h-full relative rounded-lg p-6 flex flex-col shadow-sm
        ${isSoft ? "bg-white" : "bg-white border border-gray-200"}
      `}
    >
      {/* ANGKA */}
      <div
        className={isSoft ? "text-[#D70000]" : "text-gray-900"}
        style={{
          fontFamily: "Exo, sans-serif",
          fontWeight: 700,
          fontSize: "32px",
          lineHeight: "150%",
          letterSpacing: "0.01em",
        }}
      >
        {value}
      </div>

      {/* LABEL */}
      <p
        className="mt-2 text-Grey/Grey-700"
        style={{
          fontFamily: "Exo, sans-serif",
          fontWeight: 400,
          fontSize: "14px",
          lineHeight: "150%",
          letterSpacing: "0.01em",
        }}
      >
        {label}
      </p>

      {/* ICON WATERMARK */}
      <DocIcon
        className={`
          absolute bottom-4 right-4 opacity-10
          ${iconSize}
          ${isSoft ? "text-[#D70000]" : "text-gray-[#E7E7E7]"}
        `}
      />
    </div>
  );
}
