export default function LayananUtama() {
  return (
    <section className="max-w-[1300px] mx-auto px-6 py-16">
      <h2 className="text-[26px] font-bold text-gray-900 mb-10">
        Layanan Utama BNPT
      </h2>

      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-6 gap-6">
        {layanan.slice(0, 6).map((item, index) => (
          <Card key={index} {...item} />
        ))}

        {layanan.slice(6, 11).map((item, index) => (
          <Card key={index} {...item} />
        ))}

        <div className="bg-[#f9fafb] border border-gray-100 rounded-xl p-6 flex flex-col justify-between">
          <div>
            <h3 className="text-[16px] font-bold text-gray-900 mb-2">
              Ada pertanyaan?
            </h3>
            <p className="text-[14px] text-gray-600">
              Tim kami siap membantu Anda.
            </p>
          </div>
          <a className="text-red-600 font-semibold hover:underline">
            Hubungi Sekarang â†’
          </a>
        </div>
      </div>
    </section>
  );
}

function Card({ icon, label, variant }) {
  const bg =
    variant === "primary"
      ? "bg-red-600 border-red-600"
      : variant === "soft"
      ? "bg-red-50 border-red-100"
      : "bg-[#f9fafb] border-gray-100";

  const text =
    variant === "primary"
      ? "text-white"
      : variant === "soft"
      ? "text-red-600"
      : "text-gray-800";

  const iconCircle =
    variant === "primary"
      ? "bg-white"
      : variant === "icon-red"
      ? "bg-red-600"
      : "bg-white";

  const iconColor =
    variant === "primary"
      ? "text-red-600"
      : variant === "icon-red"
      ? "text-white"
      : "text-red-600";

  return (
    <div
      className={`
        group rounded-sm border p-6 flex flex-col items-center justify-center text-center
        transition-all duration-300 cursor-pointer
        ${bg}
        hover:bg-red-600 hover:shadow-lg
      `}
    >
      <div
        className={`
          w-12 h-12 rounded-full flex items-center justify-center mb-4
          ${iconCircle}
        `}
      >
        <div className={`w-6 h-6 ${iconColor} group-hover:text-white`}>
          {icon}
        </div>
      </div>

      <p
        className={`text-[14px] font-semibold whitespace-pre-line ${text} group-hover:text-white`}
      >
        {label}
      </p>
    </div>
  );
}
